import { CONSTANTS } from "../actions";

let listId = 2;

const initialState = [
    {
        title: "Prvi",
        id: 0,
        cards: [
            {
                id: 0,
                text: "tle je tekst"
            },
            {
                id: 1,
                text: "tle je tekst2"
            }
        ]
    },
    {
        title: "Drugi",
        id: 1,
        cards: [
            {
                id: 3,
                text: "tle je tekst3"
            },
            {
                id: 4,
                text: "tle je tekst4"
            },
            {
                id: 5,
                text: "tle je tekst5"
            },
        ]
    }
]



const listReducer = (state = initialState, action) => {
    switch (action.type){
        case CONSTANTS.ADD_LIST:
            const newList = {
                title: action.payload,
                cards: [],
                id: listId
            }
            listId += 1;
            return [...state, newList];
        default:
            return state;
    }
}

export default listReducer;
