import React from "react";
import DreveskoCard from "./DreveskoCard";
import DreveskoActionButton from "./DreveskoActionButton";
import { Droppable } from "react-beautiful-dnd";
import styled from 'styled-components';

const ListContainer = styled.div`
    background-color: #dfe3e6;
    border-radius: 3;
    width: 300;
    padding: 8;
    margin-right: 10;
    height: 100%;
`

const DreveskoList = ({title, cards, listId}) => {
    return (
        <Droppable droppableId={String(listId)}>
            {(provided) =>(
                <ListContainer {...provided.droppableProps} ref={provided.innerRef} >
                <h4>{title}</h4>
                { cards.map((card, index) => <DreveskoCard key={card.id} index={index} text={card.text} id={card.id} />) }
                {provided.placeholder}
                <DreveskoActionButton listId={listId} />
                </ListContainer>
            )}
        </Droppable>
    )
};

const styles = {
    container: {
        backgroundColor: "#dfe3e6",
        borderRadius: 3,
        width: 300,
        padding: 8,
        marginRight: 10,
        height: "100%"
    }
}

export default DreveskoList;
